//  of the row by columns in descending order.
//
//  Examples:
//  coords [
//    [1 2 3 4 5 6]
//    [1 2 3 4 1 6]
//    [1 2 3 4 5 1]
//  ] 1 == [0 0 1 4 1 0 2 5 2 0]
//  coords []  1  == []
//  coords [[]  [1]  [1  2  3]]  3  == [ 2  2 ]
func coords:[]num m:[][]num n:num
    result:[]num
    for i := range (len m)
        row := m[i]
        for j := range (len row)-1 -1 -1
            if row[j] == n
                result = result + [i j]
            end
        end
    end
    return result
end

